sql.where:
deleted = 0

    AND menu_name LIKE CONCAT('%', #{menuName}, '%')


    AND parent_id = #{parentId}


    AND menu_type = #{menuType}


    AND permission LIKE CONCAT('%', #{permission}, '%')


    AND visible = #{visible}


    AND status = #{status}

mapper.insert:
INSERT INTO sys_menu (
    menu_name, parent_id, menu_type, path, component, permission, icon,
    sort_order, visible, status, create_time, update_time, create_by, update_by, deleted, remark
) VALUES (
             #{menuName}, #{parentId}, #{menuType}, #{path}, #{component}, #{permission}, #{icon},
             #{sortOrder}, #{visible}, #{status}, #{createTime}, #{updateTime}, #{createBy}, #{updateBy}, #{deleted}, #{remark}
         )

mapper.update:
UPDATE sys_menu
SET deleted = 1, update_time = NOW(), update_by = #{updateBy}
WHERE id = #{id} AND deleted = 0

UPDATE sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, menu_type = #{menuType}, path = #{path}, component = #{component}, permission = #{permission}, icon = #{icon}, sort_order = #{sortOrder}, visible = #{visible}, status = #{status}, remark = #{remark}, update_time = #{updateTime}, update_by = #{updateBy}
WHERE id = #{id} AND deleted = 0

mapper.select:
SELECT FROM sys_menu
ORDER BY ${orderBy} ${orderDirection}
ORDER BY parent_id ASC, sort_order ASC, create_time ASC

mapper.update:
UPDATE sys_menu
SET deleted = 1, update_time = NOW(), update_by = #{updateBy}
WHERE deleted = 0 AND id IN

    #{id}

mapper.select:
SELECT COUNT(1)
FROM sys_menu
WHERE deleted = 0 AND parent_id = #{parentId}
